package api

import (
	"bytes"
	"encoding/base64"
	"fmt"
	"html/template"
	"io"
	"net/http"
	"strings"
)

var brandedTemplateSrc = `<!DOCTYPE html>
<head>
<meta name="darkreader-lock">
<style type="text/css">
body, html {
	width: 100%;
	margin: 0;
	padding: 0;
	background-color: #2e3440;
	color: #d8dee9;
}
.container {
	margin: 100px;
}
header {
	width: 100%;
	height: 80px;
	background-repeat: no-repeat;
	background-size: contain;
	background-image:  url('data:image/svg+xml;base64,')
}
main {
	padding-top: 60px;
}
a {
	color: #8fbcbb;
}
</style>
</head>
<body>
	<div class="container">
		<header></header>
		<main>{{.Msg}}</main>
	</div>
</body>
</html>`

var brandedTemplate = template.Must(template.New("branded").Parse(brandedTemplateSrc))

type templateInput struct {
	Msg      template.HTML
	Hostname string
}

func (a *API) handleLocalRequest(request *http.Request) *http.Response {

	caDownload := base64.RawURLEncoding.EncodeToString(a.userSettings.Get().CACert)
	caDownloadURL := fmt.Sprintf("data:application/octet-stream;base64,%s", caDownload)

	return a.createReaperMessageResponse(request, fmt.Sprintf(`
<p>Welcome to Reaper!</p>

<p>Download the CA certificate for your proxy here: <a download="reaper-ca.crt" href="%s">Download CA Certificate</a>.</p>

<p>For further information and configuration, please use the Reaper desktop application or check out the <a target="_blank" href="https://ghostsecurity.github.io/reaper">documentation</a>.</p>

`, caDownloadURL))
}

func createRawMessageResponse(req *http.Request, data io.Reader, contentType string) *http.Response {
	return &http.Response{
		StatusCode: 200,
		Proto:      "HTTP/1.1",
		ProtoMajor: 1,
		ProtoMinor: 1,
		Request:    req,
		Body:       io.NopCloser(data),
		Header: map[string][]string{
			"Content-Type": {contentType},
		},
	}
}

func (a *API) createReaperMessageResponse(req *http.Request, msg string) *http.Response {
	buf := bytes.NewBuffer(nil)
	if err := brandedTemplate.Execute(buf, templateInput{
		Msg:      template.HTML(msg),
		Hostname: a.userSettings.Get().ProxyHost,
	}); err != nil {
		return createRawMessageResponse(req, strings.NewReader(msg), "text/plain")
	}
	return createRawMessageResponse(req, buf, "text/html")
}
